import { __decorate } from "tslib";
import { Component } from '@angular/core';
let MainComponent = class MainComponent {
    constructor(sectionService) {
        this.sectionService = sectionService;
        this.columns = 0;
        this.sections = [];
    }
    async ngOnInit() {
        this.sectionService.getSections().subscribe(sections => {
            this.sections = sections;
            this.sections.forEach(section => {
                section.cards.sort((a, b) => a.id - b.id);
            });
            this.columns = this.sections.length;
        });
    }
    removeColumn() {
        this.columns -= 1;
    }
    addSection() {
        // focus on the new section created header, and when enter, send the new section name to addSection
        this.sectionService.addSection("Section 3").subscribe(resp => {
            this.sections.push(resp);
            this.columns += 1;
        }, err => console.log(err));
    }
    addCard(event) {
        this.sections.forEach(section => {
            if (section.id == event.sectionId) {
                section.cards.push({
                    id: event.cardId,
                    section: section.id,
                    title: "New Card",
                    description: "New Description",
                    tasks: []
                });
            }
        });
    }
};
MainComponent = __decorate([
    Component({
        selector: 'app-main',
        templateUrl: './main.component.html',
        styleUrls: ['./main.component.scss']
    })
], MainComponent);
export { MainComponent };
